mapper.insert:

    SELECT LAST_INSERT_ID()

insert into tb_operation (parent_id,name,url,app_id,op_code,operator,px,created,icon,`translate`,`type`) values
(#{parentId},#{name},#{url},#{appId},#{opCode},#{operator},#{px},#{created},#{icon},#{translate},#{type});


    SELECT LAST_INSERT_ID()

insert into tb_role (name,app_id,operator,created) values
(#{name},#{appId},#{operator},#{created});

mapper.select:
SELECT a.name ,a.per_id as perId,a.app_id AS appId, (CASE a.permission WHEN 1 THEN '系统管理员' WHEN 2 THEN '应用管理员' ELSE '操作管理员' END) AS permission, (CASE a.status WHEN 0 THEN '禁用' ELSE '正常' END) AS status, a.display_name as displayName FROM t_user a where 1=1 AND a.app_id = #{appId} AND a.permission !=1
AND a.name LIKE '%${name}%'
limit #{pageSize} offset #{startRecord}
